//SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.8 <0.9.0;

import {Governable} from './Governable.sol';
import {IAdapterManagement} from '../../interfaces/peripherals/IAdapterManagement.sol';
import {IBridgeSenderAdapter} from '../../interfaces/bridges/IBridgeSenderAdapter.sol';

abstract contract AdapterManagement is IAdapterManagement, Governable {
  // TODO: write full natspec when logic is approved
  /// @inheritdoc IAdapterManagement
  mapping(IBridgeSenderAdapter => bool) public whitelistedAdapters;

  // adapter => chainId => destinationDomain
  /// @inheritdoc IAdapterManagement
  mapping(IBridgeSenderAdapter => mapping(uint16 => uint32)) public destinationDomainIds;

  // adapter => destinationDomainId => dataReceiver
  /// @inheritdoc IAdapterManagement
  mapping(IBridgeSenderAdapter => mapping(uint32 => address)) public receivers;

  /// @inheritdoc IAdapterManagement
  function whitelistAdapter(IBridgeSenderAdapter _bridgeSenderAdapter, bool _isWhitelisted) external onlyGovernor {
    _whitelistAdapter(_bridgeSenderAdapter, _isWhitelisted);
  }

  /// @inheritdoc IAdapterManagement
  function whitelistAdapters(IBridgeSenderAdapter[] calldata _bridgeSenderAdapters, bool[] calldata _isWhitelisted) external onlyGovernor {
    uint256 _bridgeSenderAdapterLength = _bridgeSenderAdapters.length;
    if (_bridgeSenderAdapterLength != _isWhitelisted.length) revert LengthMismatch();
    unchecked {
      for (uint256 _i; _i < _bridgeSenderAdapterLength; ++_i) {
        _whitelistAdapter(_bridgeSenderAdapters[_i], _isWhitelisted[_i]);
      }
    }
  }

  /// @inheritdoc IAdapterManagement
  function setDestinationDomainId(
    IBridgeSenderAdapter _bridgeSenderAdapter,
    uint16 _chainId,
    uint32 _destinationDomainId
  ) external onlyGovernor {
    _setDestinationDomainId(_bridgeSenderAdapter, _chainId, _destinationDomainId);
  }

  /// @inheritdoc IAdapterManagement
  function setDestinationDomainIds(
    IBridgeSenderAdapter[] calldata _bridgeSenderAdapters,
    uint16[] calldata _chainIds,
    uint32[] calldata _destinationDomainIds
  ) external onlyGovernor {
    uint256 _bridgeSenderAdapterLength = _bridgeSenderAdapters.length;
    if (_bridgeSenderAdapterLength != _destinationDomainIds.length || _bridgeSenderAdapterLength != _chainIds.length) revert LengthMismatch();
    unchecked {
      for (uint256 _i; _i < _bridgeSenderAdapterLength; ++_i) {
        _setDestinationDomainId(_bridgeSenderAdapters[_i], _chainIds[_i], _destinationDomainIds[_i]);
      }
    }
  }

  /// @inheritdoc IAdapterManagement
  function setReceiver(
    IBridgeSenderAdapter _bridgeSenderAdapter,
    uint32 _destinationDomainId,
    address _dataReceiver
  ) external onlyGovernor {
    _setReceiver(_bridgeSenderAdapter, _destinationDomainId, _dataReceiver);
  }

  /// @inheritdoc IAdapterManagement
  function setReceivers(
    IBridgeSenderAdapter[] calldata _bridgeSenderAdapters,
    uint32[] calldata _destinationDomainIds,
    address[] calldata _dataReceivers
  ) external onlyGovernor {
    uint256 _bridgeSenderAdapterLength = _bridgeSenderAdapters.length;
    if (_bridgeSenderAdapterLength != _destinationDomainIds.length || _bridgeSenderAdapterLength != _dataReceivers.length)
      revert LengthMismatch();
    unchecked {
      for (uint256 _i; _i < _bridgeSenderAdapterLength; ++_i) {
        _setReceiver(_bridgeSenderAdapters[_i], _destinationDomainIds[_i], _dataReceivers[_i]);
      }
    }
  }

  function validateSenderAdapter(IBridgeSenderAdapter _bridgeSenderAdapter, uint16 _chainId)
    public
    view
    returns (uint32 _destinationDomainId, address _dataReceiver)
  {
    if (!whitelistedAdapters[_bridgeSenderAdapter]) revert UnallowedAdapter();

    _destinationDomainId = destinationDomainIds[_bridgeSenderAdapter][_chainId];
    if (_destinationDomainId == 0) revert DestinationDomainIdNotSet();

    _dataReceiver = receivers[_bridgeSenderAdapter][_destinationDomainId];
    if (_dataReceiver == address(0)) revert ReceiverNotSet();
  }

  function _whitelistAdapter(IBridgeSenderAdapter _bridgeSenderAdapter, bool _isWhitelisted) internal {
    whitelistedAdapters[_bridgeSenderAdapter] = _isWhitelisted;
    emit AdapterWhitelisted(_bridgeSenderAdapter, _isWhitelisted);
  }

  function _setDestinationDomainId(
    IBridgeSenderAdapter _bridgeSenderAdapter,
    uint16 _chainId,
    uint32 _destinationDomainId
  ) internal {
    destinationDomainIds[_bridgeSenderAdapter][_chainId] = _destinationDomainId;
    emit DestinationDomainIdSet(_bridgeSenderAdapter, _chainId, _destinationDomainId);
  }

  function _setReceiver(
    IBridgeSenderAdapter _bridgeSenderAdapter,
    uint32 _destinationDomainId,
    address _dataReceiver
  ) internal {
    receivers[_bridgeSenderAdapter][_destinationDomainId] = _dataReceiver;
    emit ReceiverSet(_bridgeSenderAdapter, _destinationDomainId, _dataReceiver);
  }
}

solidity/interfaces/IDataFeed.sol.content:
//SPDX-License-Identifier: Unlicense
pragma solidity >=0.8.8 <0.9.0;

import {IUniswapV3Pool} from '@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol';
import {IAdapterManagement} from './peripherals/IAdapterManagement.sol';
import {IDataFeedKeeper} from './IDataFeedKeeper.sol';
import {IConnextSenderAdapter, IBridgeSenderAdapter, IOracleSidechain} from './bridges/IConnextSenderAdapter.sol';

interface IDataFeed is IAdapterManagement {
  // STRUCTS

  struct PoolState {
    uint24 poolNonce;
    uint32 blockTimestamp;
    int56 tickCumulative;
    int24 arithmeticMeanTick;
  }

  // STATE VARIABLES

  function keeper() external view returns (IDataFeedKeeper _keeper);

  /// @notice Tracks the last observed pool state by salt
  /// @param _poolSalt The id of both the oracle and the pool
  /// @return _lastPoolNonceObserved Nonce of the last observation
  /// @return _lastBlockTimestampObserved Last observed timestamp
  /// @return _lastTickCumulativeObserved Pool's tickCumulative at last observed timestamp
  /// @return _lastArithmeticMeanTickObserved Last observed arithmeticMeanTick
  function lastPoolStateObserved(bytes32 _poolSalt)
    external
    view
    returns (
      uint24 _lastPoolNonceObserved,
      uint32 _lastBlockTimestampObserved,
      int56 _lastTickCumulativeObserved,
      int24 _lastArithmeticMeanTickObserved
    );

  // EVENTS

  event DataSent(
    IBridgeSenderAdapter _bridgeSenderAdapter,
    address _dataReceiver,
    uint32 _destinationDomainId,
    IOracleSidechain.ObservationData[] _observationsData,
    bytes32 _poolSalt
  );

  event PoolObserved(bytes32 _poolSalt, uint24 _poolNonce, IOracleSidechain.ObservationData[] _observationsData);

  event KeeperUpdated(IDataFeedKeeper _keeper);

  // ERRORS

  error InvalidSecondsAgos();

  error UnknownHash();

  error OnlyKeeper();

  // FUNCTIONS

  function sendObservations(
    IBridgeSenderAdapter _bridgeSenderAdapter,
    uint16 _chainId,
    bytes32 _poolSalt,
    uint24 _poolNonce,
    IOracleSidechain.ObservationData[] calldata _observationsData
  ) external;

  function fetchObservations(bytes32 _poolSalt, uint32[] calldata _secondsAgos) external;

  function fetchObservationsIndices(IUniswapV3Pool _pool, uint32[] calldata _secondsAgos)
    external
    view
    returns (uint16[] memory _observationsIndices);

  function setKeeper(IDataFeedKeeper _keeper) external;
}

solidity/libraries/OracleFork.sol.content:
//SPDX-License-Identifier: BUSL-1.1
pragma solidity ^0.8.0;

import {IUniswapV3Pool} from '@uniswap/v3-core/contracts/interfaces/IUniswapV3Pool.sol';

/// @title OracleFork
/// @notice Provides price and liquidity data useful for a wide variety of system designs
/// @dev Instances of stored oracle data, "observations", are collected in the oracle array
/// Every pool is initialized with an oracle array length of 1. Anyone can pay the SSTOREs to increase the
/// maximum length of the oracle array. New slots will be added when the array is fully populated.
/// Observations are overwritten when the full length of the oracle array is populated.
/// The most recent observation is available, independent of the length of the oracle array, by passing 0 to observe()
library OracleFork {
  error I();
  error OLD();

  /// @notice comparator for 32-bit timestamps
  /// @dev safe for 0 or 1 overflows, a and b _must_ be chronologically before or equal to time
  /// @param _time A timestamp truncated to 32 bits
  /// @param _a A comparison timestamp from which to determine the relative position of `time`
  /// @param _b From which to determine the relative position of `time`
  /// @return Whether `a` is chronologically <= `b`
  function _lte(
    uint32 _time,
    uint32 _a,
    uint32 _b
  ) private pure returns (bool) {
    unchecked {
      // if there hasn't been overflow, no need to adjust
      if (_a <= _time && _b <= _time) return _a <= _b;

      uint256 _aAdjusted = _a > _time ? _a : _a + 2**32;
      uint256 _bAdjusted = _b > _time ? _b : _b + 2**32;

      return _aAdjusted <= _bAdjusted;
    }
  }

  /// @notice Fetches the observations beforeOrAt and atOrAfter a target, i.e. where [beforeOrAt, atOrAfter] is satisfied.
  /// The result may be the same observation, or adjacent observations.
  /// @dev The answer must be contained in the array, used when the target is located within the stored observation
  /// boundaries: older than the most recent observation and younger, or the same age as, the oldest observation
  /// @param _pool The Uniswap V3 pool
  /// @param _time The current block.timestamp
  /// @param _target The timestamp at which the reserved observation should be for
  /// @param _index The index of the observation that was most recently written to the observations array
  /// @param _cardinality The number of populated elements in the oracle array
  /// @return _beforeOrAtIndex The index of the observation recorded before, or at, the target
  function _binarySearch(
    IUniswapV3Pool _pool,
    uint32 _time,
    uint32 _target,
    uint16 _index,
    uint16 _cardinality
  ) private view returns (uint16 _beforeOrAtIndex) {
    unchecked {
      uint16 _atOrAfterIndex;
      uint256 _l = (_index + 1) % _cardinality; // oldest observation
      uint256 _r = _l + _cardinality - 1; // newest observation
      uint256 _i;
      uint32 _blockTimestamp;
      bool _initialized;
      while (true) {
        _i = (_l + _r) / 2;

        _beforeOrAtIndex = uint16(_i % _cardinality);
        (_blockTimestamp, , , _initialized) = _pool.observations(_beforeOrAtIndex);

        // we've landed on an uninitialized tick, keep searching higher (more recently)
        if (!_initialized) {
          _l = _i + 1;
          continue;
        }

        if (!_lte(_time, _blockTimestamp, _target)) {
          _r = _i - 1;
          continue;
        }

        _atOrAfterIndex = uint16((_i + 1) % _cardinality);
        (_blockTimestamp, , , ) = _pool.observations(_atOrAfterIndex);

        // check if we've found the answer!
        if (!_lte(_time, _target, _blockTimestamp)) {
          _l = _i + 1;
          continue;
        }

        if (_target == _blockTimestamp) _beforeOrAtIndex = _atOrAfterIndex;
        break;
      }
    }
  }

  /// @notice Fetches the observations beforeOrAt and atOrAfter a given target, i.e. where [beforeOrAt, atOrAfter] is satisfied
  /// @dev Assumes there is at least 1 initialized observation.
  /// @param _pool The Uniswap V3 pool
  /// @param _time The current block.timestamp
  /// @param _target The timestamp at which the reserved observation should be for
  /// @param _index The index of the observation that was most recently written to the observations array
  /// @param _cardinality The number of populated elements in the oracle array
  /// @return _beforeOrAtIndex The index of the observation which occurred at, or before, the given timestamp
  function getPreviousObservationIndex(
    IUniswapV3Pool _pool,
    uint32 _time,
    uint32 _target,
    uint16 _index,
    uint16 _cardinality
  ) internal view returns (uint16 _beforeOrAtIndex) {
    unchecked {
      if (_cardinality <= 0) revert I();

      // optimistically fetch the newest observation
      (uint32 _blockTimestamp, , , ) = _pool.observations(_index);

      // if the target is chronologically at or after the newest observation, we can early return
      if (_lte(_time, _blockTimestamp, _target)) {
        return _index;
      }

      // now, fetch the oldest observation
      bool _initialized;
      (_blockTimestamp, , , _initialized) = _pool.observations((_index + 1) % _cardinality);
      if (!_initialized) {
        (_blockTimestamp, , , ) = _pool.observations(0);
      }

      // ensure that the target is chronologically at or after the oldest observation
      if (!_lte(_time, _blockTimestamp, _target)) revert OLD();

      // if we've reached this point, we have to binary search
      return _binarySearch(_pool, _time, _target, _index, _cardinality);
    }
  }
}

solidity/libraries/Create2Address.sol.content:
//SPDX-License-Identifier: BUSL-1.1
pragma solidity >=0.8.8 <0.9.0;

/// @title Provides functions for deriving a pool address from the factory, tokens, and the fee
library Create2Address {
  /// @notice Deterministically computes the pool address given the factory, salt and initCodeHash
  /// @param _factory The Uniswap V3 factory contract address
  /// @param _salt The PoolKey encoded bytes
  /// @param _initCodeHash The Init Code Hash of the target
  /// @return _pool The contract address of the target pool/oracle
  function computeAddress(
    address _factory,
    bytes32 _salt,
    bytes32 _initCodeHash
  ) internal pure returns (address _pool) {
    _pool = address(uint160(uint256(keccak256(abi.encodePacked(hex'ff', _factory, _salt, _initCodeHash)))));
  }
}

